int varia_sum(int count, ...)
{
    va_list args;
    int total = 0;

    __builtin_va_start((args));

    for (int i = 0; i < count; i++)
    {
        total += __builtin_va_arg((args));
    }

int deplacement_aleatoire(int min, int max)
{
    return min + (int)(rand() / (32767 + 0.001) * (max - min + 1));
}

