int varia_find_min(int count, ...)
{
	va_list args;
	__builtin_va_start((args));

	int min = __builtin_va_arg((args));
	for (int i = 1; i < count; i++)
	{
		int num = __builtin_va_arg((args));
		if (num < min)
		{
			min = num;
		}

